mapper.select:
select * from (select e.*, row_number() over(order by faq_id desc) as rn from (select faq_id,faq_category,faq_title,faq_content,faq_write_date,faq_view_count,member_id, (select member_username from member where faq.member_id=member.member_id) as member_username from faq order by faq_id desc) e
${select} like '%'||#{keyword}||'%'
) where rn between #{start} and #{end}
